import React, { useState, useEffect } from 'react';
import ZenTree from './ZenTree';
import authService from '../services/AuthService';
import { getUserInterventionStats, getUserAssessments, saveAssessmentResult } from './firestoreHelpers';
import { TrendingUp, Target, Clock, Award } from './icons';

const moodOptions = [
	{ value: 1, label: '😔', text: 'Bajo' },
	{ value: 2, label: '😕', text: 'Inquieto' },
	{ value: 3, label: '😐', text: 'Neutral' },
	{ value: 4, label: '🙂', text: 'Mejor' },
	{ value: 5, label: '😊', text: 'Positivo' }
];

const InicioScreen = ({ onStartTest }) => {
	const [user, setUser] = useState(null);
	const [stats, setStats] = useState(null);
	const [assessments, setAssessments] = useState([]);
	const [savingMood, setSavingMood] = useState(false);
	const [lastMood, setLastMood] = useState(null);
	const [timeOfDay, setTimeOfDay] = useState('');

	useEffect(() => {
		// Greeting logic
		const hour = new Date().getHours();
		if (hour < 12) setTimeOfDay('Buenos días');
		else if (hour < 19) setTimeOfDay('Buenas tardes');
		else setTimeOfDay('Buenas noches');

		const unsubscribe = authService.initialize(async currentUser => {
			setUser(currentUser);
			if (currentUser) {
				const s = await getUserInterventionStats();
				setStats(s);
				const a = await getUserAssessments();
				setAssessments(a || []);
				if (a && a.length) setLastMood(a[0]);
			}
		});
		return () => unsubscribe && unsubscribe();
	}, []);

	const handleQuickMood = async (value) => {
		if (!user || savingMood) return;
		try {
			setSavingMood(true);
			const moodObj = { score: value, context: 'quick-mood', createdManual: true };
			await saveAssessmentResult(moodObj);
			setLastMood({ ...moodObj, score: value, date: new Date().toISOString() });
			// Prepend to assessments for immediate UI feedback
			setAssessments(prev => [{ ...moodObj, score: value, date: new Date().toISOString() }, ...prev]);
		} catch (e) {
			console.error('Error saving mood', e);
		} finally {
			setSavingMood(false);
		}
	};

	const weeklyAverage = assessments.length
		? (assessments.slice(0, 7).reduce((sum, a) => sum + (a.score || 0), 0) / Math.min(7, assessments.slice(0, 7).length)).toFixed(1)
		: null;

	const favoriteTool = stats?.favoriteTools && Object.keys(stats.favoriteTools).length
		? Object.keys(stats.favoriteTools).reduce((a, b) => stats.favoriteTools[a] > stats.favoriteTools[b] ? a : b)
		: null;

	const lastMoodEmoji = lastMood ? moodOptions.find(m => m.value === lastMood.score)?.label : '—';

	return (
		<div className="p-6 space-y-6 animate-fade-in">
			{/* Greeting */}
			<div className="text-center space-y-1">
				<h1 className="text-3xl font-bold text-slate-800">
					{timeOfDay}{user ? `, ${user.displayName || 'amig@'}` : ''}
				</h1>
				<p className="text-slate-500">Tómate un momento para reconectar contigo.</p>
			</div>

			{/* Zen Visual */}
			<ZenTree />

			{/* Quick Mood & Key Stats */}
			<div className="grid grid-cols-1 md:grid-cols-3 gap-4">
				<div className="bg-white/80 p-4 rounded-2xl shadow-sm flex flex-col">
					<h2 className="font-bold text-slate-700 mb-2">Estado Rápido</h2>
					<div className="flex justify-between mb-3">
						{moodOptions.map(m => (
							<button
								key={m.value}
								onClick={() => handleQuickMood(m.value)}
								disabled={savingMood}
								className={`text-2xl transition-transform hover:scale-110 ${lastMood?.score === m.value ? 'opacity-100' : 'opacity-60'}`}
								title={m.text}
							>{m.label}</button>
						))}
					</div>
					<div className="text-xs text-slate-500 mt-auto">
						Último: {lastMoodEmoji !== '—' ? `${lastMoodEmoji} hace poco` : 'sin registro'}
					</div>
				</div>

				<div className="bg-white/80 p-4 rounded-2xl shadow-sm flex flex-col items-center justify-center">
					<div className="flex items-center gap-2 mb-1 text-violet-600">
						<TrendingUp className="w-4 h-4" />
						<span className="text-sm font-semibold">Promedio Semana</span>
					</div>
					<div className="text-3xl font-bold text-slate-800">{weeklyAverage || '—'}</div>
					<div className="text-xs text-slate-500">/5 bienestar</div>
				</div>

				<div className="bg-white/80 p-4 rounded-2xl shadow-sm flex flex-col items-center justify-center">
					<div className="flex items-center gap-2 mb-1 text-emerald-600">
						<Clock className="w-4 h-4" />
						<span className="text-sm font-semibold">Sesiones</span>
					</div>
						<div className="text-3xl font-bold text-slate-800">{stats?.totalSessions || 0}</div>
						<div className="text-xs text-slate-500">total</div>
				</div>
			</div>

			{/* CTA Cards */}
			<div className="grid grid-cols-1 md:grid-cols-2 gap-4">
				<div className="bg-gradient-to-br from-violet-500 to-purple-600 text-white p-5 rounded-2xl shadow-sm flex flex-col">
					<h2 className="font-bold mb-1">Análisis Rápido</h2>
					<p className="text-sm text-violet-100 mb-3">Evalúa tu estado emocional actual.</p>
					<button
						onClick={onStartTest}
						className="mt-auto bg-white/20 hover:bg-white/30 text-sm font-semibold py-2 rounded-lg transition-colors"
					>Comenzar Test</button>
				</div>

				<div className="bg-white/80 p-5 rounded-2xl shadow-sm flex flex-col">
					<h2 className="font-bold text-slate-700 mb-1">Tu Progreso</h2>
					<p className="text-sm text-slate-500 mb-3">{favoriteTool ? `Herramienta favorita: ${favoriteTool}` : 'Empieza a usar las herramientas de calma.'}</p>
					<div className="flex items-center gap-4 mt-auto">
						<div className="flex flex-col items-center">
							<Target className="w-5 h-5 text-violet-500" />
							<span className="text-xs text-slate-500 mt-1">{stats?.totalSessions || 0} sesiones</span>
						</div>
						<div className="flex flex-col items-center">
							<Award className="w-5 h-5 text-amber-500" />
							<span className="text-xs text-slate-500 mt-1">{stats ? Math.round((stats.totalMinutes || 0) / 60) : 0}h</span>
						</div>
						<div className="flex flex-col items-center">
							<TrendingUp className="w-5 h-5 text-emerald-500" />
							<span className="text-xs text-slate-500 mt-1">Prom {weeklyAverage || '—'}</span>
						</div>
					</div>
				</div>
			</div>

			{/* Mindful Tip */}
			<div className="text-center text-xs text-slate-500">
				Consejo: Un pequeño respiro consciente puede cambiar el ritmo de tu día.
			</div>
		</div>
	);
};

export default InicioScreen;
