import React, { useState, useEffect, useMemo } from 'react';
import ZenTree from './ZenTree';
import authService from '../services/AuthService';
import { getUserInterventionStats, getUserAssessments, saveAssessmentResult, getUserPreferences } from './firestoreHelpers';
import { TrendingUp, Target, Clock, Award, Wind, MessageSquare } from './icons';

const moodOptions = [
  { value: 1, label: 'ðŸ˜”', text: 'Bajo' },
  { value: 2, label: 'ðŸ˜•', text: 'Inquieto' },
  { value: 3, label: 'ðŸ˜', text: 'Neutral' },
  { value: 4, label: 'ðŸ™‚', text: 'Mejor' },
  { value: 5, label: 'ðŸ˜Š', text: 'Positivo' }
];

const mindfulTips = [
  "Un pequeÃ±o respiro consciente puede cambiar el ritmo de tu dÃ­a.",
  "Observa tus pensamientos sin juzgarlos, como nubes en el cielo.",
  "Siente tus pies en el suelo para anclarte en el presente.",
  "La amabilidad hacia ti mismo es tan importante como hacia los demÃ¡s.",
  "Escucha atentamente los sonidos a tu alrededor por un minuto."
];

const SkeletonLoader = () => (
  <div className="bg-slate-200/50 dark:bg-slate-700/50 p-4 rounded-2xl shadow-sm animate-pulse h-full">
    <div className="h-4 bg-slate-300 dark:bg-slate-600 rounded w-3/4 mb-2"></div>
    <div className="h-8 bg-slate-300 dark:bg-slate-600 rounded w-1/2"></div>
  </div>
);

const InicioScreen = ({ onStartTest, onStartBreathing }) => {
  const [user, setUser] = useState(null);
  const [stats, setStats] = useState(null);
  const [assessments, setAssessments] = useState([]);
  const [savingMood, setSavingMood] = useState(false);
  const [lastMood, setLastMood] = useState(null);
  const [timeOfDay, setTimeOfDay] = useState('');
  const [loading, setLoading] = useState(true);
  const [darkMode, setDarkMode] = useState(false);

  const dailyTip = useMemo(() => mindfulTips[new Date().getDate() % mindfulTips.length], []);

  useEffect(() => {
    const hour = new Date().getHours();
    if (hour < 12) setTimeOfDay('Buenos dÃ­as');
    else if (hour < 19) setTimeOfDay('Buenas tardes');
    else setTimeOfDay('Buenas noches');

    const unsubscribe = authService.initialize(async currentUser => {
      setUser(currentUser);
      if (currentUser) {
        setLoading(true);
        const prefs = await getUserPreferences();
        setDarkMode(prefs.darkMode || false);

        const s = await getUserInterventionStats();
        setStats(s);
        const a = await getUserAssessments();
        setAssessments(a || []);
        if (a && a.length) setLastMood(a[0]);
        setLoading(false);
      } else {
        setLoading(false);
      }
    });
    return () => unsubscribe && unsubscribe();
  }, []);

  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, [darkMode]);

  const handleQuickMood = async (value) => {
    if (!user || savingMood) return;
    try {
      setSavingMood(true);
      const moodObj = { score: value, context: 'quick-mood', createdManual: true };
      await saveAssessmentResult(moodObj);
      const newAssessment = { ...moodObj, score: value, createdAt: new Date().toISOString() };
      setLastMood(newAssessment);
      setAssessments(prev => [newAssessment, ...prev]);
    } catch (e) {
      console.error('Error saving mood', e);
    } finally {
      setSavingMood(false);
    }
  };

  const formatTimeAgo = (timestamp) => {
    if (!timestamp) return 'hace poco';
    const now = new Date();
    const time = new Date(timestamp);
    const diffMs = now - time;
    const diffMins = Math.floor(diffMs / (1000 * 60));
    if (diffMins < 60) return `hace ${diffMins} min`;
    return `hace ${Math.floor(diffMins / 60)}h`;
  };

  const weeklyAverage = assessments.length
    ? (assessments.slice(0, 7).reduce((sum, a) => sum + (a.score || 0), 0) / Math.min(7, assessments.length)).toFixed(1)
    : null;

  const lastMoodEmoji = lastMood ? moodOptions.find(m => m.value === lastMood.score)?.label : 'â€”';
  const currentStreak = stats?.totalSessions ? Math.min(stats.totalSessions, 14) : 0;

  return (
    <div className="p-6 space-y-6 animate-fade-in dark:bg-slate-900 dark:text-slate-200">
      {/* Greeting */}
      <div className="text-center space-y-1">
        <h1 className="text-3xl font-bold text-slate-800 dark:text-white">
          {timeOfDay}{user ? `, ${user.displayName || 'amig@'}` : ''}
        </h1>
        <p className="text-slate-500 dark:text-slate-400">TÃ³mate un momento para reconectar contigo.</p>
      </div>

      {/* Zen Visual */}
      <ZenTree />

      {/* Quick Mood & Key Stats */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <div className="bg-white/80 dark:bg-slate-800/80 p-4 rounded-2xl shadow-sm flex flex-col">
          <h2 className="font-bold text-slate-700 dark:text-slate-200 mb-2">Estado RÃ¡pido</h2>
          <div className="flex justify-between mb-3">
            {moodOptions.map(m => (
              <button
                key={m.value}
                onClick={() => handleQuickMood(m.value)}
                disabled={savingMood}
                className={`text-2xl transition-transform hover:scale-110 ${lastMood?.score === m.value ? 'opacity-100' : 'opacity-60 dark:opacity-40'}`}
                title={m.text}
              >{m.label}</button>
            ))}
          </div>
          <div className="text-xs text-slate-500 dark:text-slate-400 mt-auto">
            Ãšltimo: {lastMoodEmoji !== 'â€”' ? `${lastMoodEmoji} ${formatTimeAgo(lastMood.createdAt)}` : 'sin registro'}
          </div>
        </div>

        {loading ? <SkeletonLoader /> : (
          <div className="bg-white/80 dark:bg-slate-800/80 p-4 rounded-2xl shadow-sm flex flex-col items-center justify-center">
            <div className="flex items-center gap-2 mb-1 text-violet-600 dark:text-violet-400">
              <TrendingUp className="w-4 h-4" />
              <span className="text-sm font-semibold">Promedio Semana</span>
            </div>
            <div className="text-3xl font-bold text-slate-800 dark:text-white">{weeklyAverage || 'â€”'}</div>
            <div className="text-xs text-slate-500 dark:text-slate-400">/5 bienestar</div>
          </div>
