import React, { useState, useEffect } from 'react';
import { getUserPreferences, saveUserPreferences } from './firestoreHelpers';
import authService from '../services/AuthService';

const AjustesScreen = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [notifications, setNotifications] = useState(true);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const unsubscribe = authService.initialize(currentUser => {
      setUser(currentUser);
      if (currentUser) {
        getUserPreferences().then(prefs => {
          setDarkMode(prefs.darkMode || false);
        });
      }
    });
    return () => unsubscribe && unsubscribe();
  }, []);

  useEffect(() => {
    if (user) {
        if (darkMode) {
            document.documentElement.classList.add('dark');
        } else {
            document.documentElement.classList.remove('dark');
        }
        saveUserPreferences({ darkMode });
    }
  }, [darkMode, user]);

  const Toggle = ({ isEnabled, onToggle }) => (
    <div 
      onClick={() => onToggle(!isEnabled)}
      className={`w-12 h-6 rounded-full cursor-pointer p-1 transition-colors ${isEnabled ? 'bg-violet-600' : 'bg-slate-300 dark:bg-slate-600'}`}
    >
      <div className={`w-4 h-4 bg-white rounded-full transition-transform ${isEnabled ? 'transform translate-x-6' : ''}`}></div>
    </div>
  );

  return (
    <div className="p-6 space-y-4 animate-fade-in dark:bg-slate-900 dark:text-slate-200 h-full">
      <h1 className="text-3xl font-bold text-slate-800 dark:text-white mb-4">Ajustes</h1>

      <div className="bg-white/80 dark:bg-slate-800/80 p-4 rounded-2xl shadow-sm flex justify-between items-center">
        <span className="font-semibold text-slate-700 dark:text-slate-200">Notificaciones</span>
        <Toggle isEnabled={notifications} onToggle={setNotifications} />
      </div>

      <div className="bg-white/80 dark:bg-slate-800/80 p-4 rounded-2xl shadow-sm flex justify-between items-center">
        <span className="font-semibold text-slate-700 dark:text-slate-200">Modo Oscuro</span>
        <Toggle isEnabled={darkMode} onToggle={setDarkMode} />
      </div>

      <div className="bg-white/80 dark:bg-slate-800/80 p-4 rounded-2xl shadow-sm">
        <p className="text-sm text-slate-600 dark:text-slate-300">Privacidad de Datos</p>
        <p className="text-xs text-slate-500 dark:text-slate-400 mt-1">Gestiona cómo se utilizan tus datos para mejorar tu experiencia.</p>
      </div>

      {user && (
          <div className="pt-8 text-center">
              <button 
                onClick={() => authService.logout()}
                className="bg-red-500/10 text-red-500 font-semibold py-2 px-6 rounded-lg hover:bg-red-500/20 transition-colors"
              >
                Cerrar Sesión
              </button>
          </div>
      )}
    </div>
  );
};

export default AjustesScreen;
